int x=1,y=1;

void printMap(int linNum,int lieNum);
void move(int xAdd,int yAdd);
void goToXY(short wx,short wy);
void HideCursor();

int main() {

    system("cls");
    HideCursor();
    printMap(50,50);
    while(1)
    {
        int tem = _getch();
        int ipt;
        if(tem  == 224)
            ipt = _getch();
        else
            ipt = tem;
        switch (ipt)
        {
            case 72:
            case 119:
                move(-1,0);
                break;
            case 80:
            case 115:
                move(1,0);
                break;
            case 75:
            case 97:
                move(0,-1);
                break;
            case 77:
            case 100:
                move(0,1);
                break;
            default: continue;
        }
        if(maze[x][y] == 2)
        {
            printf("success!");
            return 0;
        }
        goToXY(0,0);
        HideCursor();
        printMap(50,50);
    }
}

void printMap(int linNum,int lieNum)
{
    for(int i = 0; i < linNum ; ++i)
    {
        for (int j = 0; j < lieNum; ++j)
        {
            switch(maze[i][j])
            {
                case 0:
                    printf(" ");
                    break;
                case 1:
                    printf("#");
                    break;
                case 2:
                    printf("E");
                    break;
                case 3:
                    printf("A");
                    break;
                default:
                    printf("?");
                    break;
            }
        }
        printf("\n");
    }

}


void move(int xAdd,int yAdd)
{
    int temX = x;
    int temY = y;
    temX += xAdd;
    temY += yAdd;
    if(maze[temX][temY] == 0)
    {
        maze[x][y] = 0;
        maze[temX][temY] = 3;
        x = temX;
        y = temY;
    }
    if(maze[temX][temY] == 2)
    {
        maze[x][y] = 0;
        x = temX;
        y = temY;
    }
}


void goToXY(short wx,short wy)    //The following code contains copied elements
{
    HANDLE hOutput;
    COORD loc;
    loc.X= wx;
    loc.Y=wy;
    hOutput= GetStdHandle(STD_OUTPUT_HANDLE);
    SetConsoleCursorPosition(hOutput, loc);
}

void HideCursor()

{

    CONSOLE_CURSOR_INFO cursor_info = {1, 0};
    SetConsoleCursorInfo(GetStdHandle(STD_OUTPUT_HANDLE), &cursor_info);

}
